import React, { useState } from 'react';
import './Contact.scss';

const Contact = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isConfirmed, setIsConfirmed] = useState(false);

  const handleOpenModal = () => setIsModalOpen(true);
  const handleCloseModal = () => {
    setIsModalOpen(false);
    setIsConfirmed(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = {
      name: e.target.name.value,
      email: e.target.email.value,
      message: e.target.message.value,
    };

    try {
      const response = await fetch('http://localhost:5000/api/contact', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setIsConfirmed(true);
        setTimeout(() => {
          handleCloseModal();
          window.location.href = '/'; // Redirige vers la page d'accueil
        }, 3000); // Délai de 3 secondes avant la redirection
      } else {
        alert("Erreur lors de l'envoi du message.");
      }
    } catch (error) {
      console.error('Erreur:', error);
      alert("Erreur lors de l'envoi du message.");
    }
  };

  return (
    <div className="contact">
      <button className="contact-button" onClick={handleOpenModal}>
        Contactez-moi
      </button>

      {isModalOpen && (
        <div className={`modal ${isConfirmed ? 'modal-confirmation' : ''}`}>
          <div className="modal-content">
            {isConfirmed ? (
              <div className="confirmation-message">
                <div className="confirmation-icon">&#10003;</div>
                <h2>Merci, nous vous recontacterons dès que possible.</h2>
              </div>
            ) : (
              <>
                <span className="close" onClick={handleCloseModal}>&times;</span>
                <h2>Contactez-moi</h2>
                <form onSubmit={handleSubmit}>
                  <label htmlFor="name">Nom :</label>
                  <input type="text" id="name" name="name" required />

                  <label htmlFor="email">Adresse e-mail :</label>
                  <input type="email" id="email" name="email" required />

                  <label htmlFor="message">Votre message :</label>
                  <textarea id="message" name="message" required></textarea>

                  <button type="submit" className="submit-button">Me contacter</button>
                </form>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Contact;
