import React, { useContext, useEffect } from 'react';
import './styles/App.scss';
import Loading from './components/Loading';
import AppRouter from './AppRouter';
import { LoadingProvider, LoadingContext } from './contexts/LoadingContext';
import { BrowserRouter as Router, useLocation } from 'react-router-dom';

function App() {
  const { isFirstLoad, setIsFirstLoad } = useContext(LoadingContext);
  const location = useLocation();

  useEffect(() => {
    if (isFirstLoad && location.pathname === '/') {
      setTimeout(() => setIsFirstLoad(false), 5000);
    } else if (location.pathname !== '/') {
      setIsFirstLoad(false);
    }
  }, [isFirstLoad, location.pathname, setIsFirstLoad]);

  return isFirstLoad && location.pathname === '/' ? <Loading /> : <AppRouter />;
}

export default function AppWrapper() {
  return (
    <Router>
      <LoadingProvider>
        <App />
      </LoadingProvider>
    </Router>
  );
}
